using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;using API.Services;using Microsoft.AspNetCore.Mvc;// For more information on enabling MVC for empty projects, visit https://go.microsoft.com/fwlink/?LinkID=397860namespace API.Controllers{    [ApiController]    [Route("api/[controller]")]    public class JobsController : ControllerBase    {        private readonly JobService _jobService;        public JobsController(JobService jobService)        {            _jobService = jobService;        }        [HttpGet]        public IActionResult GetJobs()        {            var jobs = _jobService.GetAllJobs();            return Ok(jobs);        }        [HttpGet("{jobName}")]        public IActionResult GetJobByName(string jobName)        {            var job = _jobService.GetJobByName(jobName);            if(job == null)            {                return NotFound($"Job {jobName} was not found");            }            return Ok(job);        }    }}