using System;using API.DTOs;using Core.Jobs;namespace API.Services{    public class JobService    {        public List<JobDto> GetAllJobs()        {            return new List<JobDto>            {                new JobDto                {                    Name = "Warrior",                    Description = "On the northernmost edge of Abalathia's Spine exists a mountain tribe renowned for producing fearsome mercenaries. Wielding greataxes and known as warriors, these men and women learn to harness their inner-beasts and translate that power to unbridled savagery on the battlefield.",                    Actions = new List<ActionDto>                    {                        new ActionDto                        {                            Name = "Heavy Swing",                            Description = "Delivers a physical attack with a potency of 15",                            Cooldown = 2,                            Potency = 15,                            RequiredLevel = 1                        }                    },                    Abilities = new List<AbilityDto>                    {                        new AbilityDto                        {                            Name = "Rampart",                            Description = "Reduces damage taken by 20%",                            Cooldown = 90,                            Effect = "Reduces damage taken by 20%",                            RequiredLevel = 8,                        }                    }                }            };        }        public JobDto? GetJobDetails(string jobName)        {            return GetAllJobs().FirstOrDefault(job => job.Name.Equals(jobName, StringComparison.OrdinalIgnoreCase));        }    }}